import React, { useRef, useState } from "react";
import "./../App.css";
import { login, signup } from "../API/registrationApi";

const RegistrationForm = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const Name = useRef();
  const contactNumber = useRef();
  const password = useRef();
  const confirmPassword = useRef();

  //const handleChange = (e) => {
  //  const { name, data } = e.target;
  //  switch (name) {
  //    //case "name":
  //    //Name.current.value = data;
  //    //break;
  //    case "contactNumber":
  //      contactNumber.current.value = data;
  //      break;
  //    case "password":
  //      password.current.value = data;
  //      break;
  //    case "confirmPassword":
  //      confirmPassword.current.value = data;
  //      break;
  //    default:
  //      break;
  //  }
  //};

  const handleSubmit = (e) => {
    e.preventDefault();
    let response = isSignUp
      ? signup({
          name: Name.current.value,
          contactNumber: contactNumber.current.value,
          password: password.current.value,
          confirmPassword: confirmPassword.current.value,
        })
      : login({
          contactNumber: contactNumber.current.value,
          password: password.current.value,
        });

    console.log(response);
    //do this inCase of success response
  };

  return (
    <div className="modal-container">
      <div className="register-login">
        <form onSubmit={handleSubmit}>
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Name</label>
            <input type="text" ref={Name} name="name" />
          </section>
          <label>Contact</label>
          <input type="text" ref={contactNumber} name="contact" />
          <label>Password</label>
          <input type="text" ref={password} name="password" />
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Confirm Password</label>
            <input type="text" ref={confirmPassword} name="confirmPassword" />
          </section>
          <section>
            <button type="submit">Submit</button>
          </section>
        </form>
      </div>
      <div className="response-info"></div>
    </div>
  );
};

export default RegistrationForm;
