import React, { useRef, useState } from "react";
import "./../App.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { login, signup } from "../API/registrationApi";

const RegistrationForm = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const [res, setRes] = useState(false);
  const Name = useRef();
  const contactNumber = useRef();
  const password = useRef();
  const confirmPassword = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    let response;
    if (isSignUp) {
      response = await signup({
        name: Name.current.value,
        contactNumber: contactNumber.current.value,
        password: password.current.value,
        confirmPassword: confirmPassword.current.value,
      });
    } else {
      response = await login({
        contactNumber: contactNumber.current.value,
        password: password.current.value,
      });
    }

    if (response.data?.errors) {
      setRes(true);
      toast.error("signup failed", {
        position: toast.POSITION.TOP_RIGHT,
      });
      console.log(response.data.errors);
    } else {
      setRes(true);
      toast.success("Signup success", {
        position: toast.POSITION.TOP_RIGHT,
      });
      console.log("signup success");
      setIsSignUp((prev) => !prev);
    }
  };

  return (
    <div className="modal-container">
      <div className="register-login">
        <div className="toggel-button">
          <button
            className="button active"
            onClick={() => {
              setIsSignUp((prev) => (prev = true));
            }}
          >
            signup
          </button>
          <button
            className="button active"
            onClick={() => {
              setIsSignUp((prev) => (prev = false));
            }}
          >
            Login
          </button>
        </div>
        <form onSubmit={handleSubmit}>
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Name</label>
            <input type="text" ref={Name} />
          </section>
          <label>Contact</label>
          <input type="text" ref={contactNumber} />
          <label>Password</label>
          <input type="password" ref={password} />
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Confirm Password</label>
            <input type="password" ref={confirmPassword} />
          </section>
          <section>
            <button type="submit">Submit</button>
          </section>
        </form>
      </div>
      {response ? <div className="response-info">{response.data}</div> : null}
    </div>
  );
};

export default RegistrationForm;
