import { getRequest } from "./helper/get";
import { postRequest } from "./helper/post";
let addUser = "https://localhost:3000/user/add-user";
let loginUser = "https://localhost:3000/user/login";
let header =
  ("Content-Type: application/json", `Authorization: Bearer ${token}`);
export const signup = async (data) => {
  let response = await postRequest(addUser, header, data);
  return response;
};

export const login = async (data) => {
  let response = await getRequest(loginUser, header, data);
  return response;
};
