import React, { useRef, useState } from "react";
import "./../App.css";
import { login, signup } from "../API/registrationApi";

const RegistrationForm = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const Name = useRef();
  const contactNumber = useRef();
  const password = useRef();
  const confirmPassword = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    let response = isSignUp
      ? await signup({
          name: Name.current.value,
          contactNumber: contactNumber.current.value,
          password: password.current.value,
          confirmPassword: confirmPassword.current.value,
        })
      : await login({
          contactNumber: contactNumber.current.value,
          password: password.current.value,
        });

    console.log(response.data);
    if (response?.data?.errors) {
      console.log("signup failed");
    } else {
      console.log("signup successfull");
      setIsSignUp((prev) => !prev);
    }
    //do this inCase of success response
  };

  return (
    <div className="modal-container">
      <div className="register-login">
        <form onSubmit={handleSubmit}>
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Name</label>
            <input type="text" ref={Name} />
          </section>
          <label>Contact</label>
          <input type="text" ref={contactNumber} />
          <label>Password</label>
          <input type="text" ref={password} />
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Confirm Password</label>
            <input type="text" ref={confirmPassword} />
          </section>
          <section>
            <button type="submit">Submit</button>
          </section>
        </form>
      </div>
      <div className="response-info"></div>
    </div>
  );
};

export default RegistrationForm;
