import React, { useRef, useState } from "react";
import "./../App.css";

const RegistrationForm = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const Name = useRef();
  const contactNumber = useRef();
  const password = useRef();
  const confirmPassword = useRef();

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "name":
        Name.current.value = value;
        break;
      case "contactNumber":
        contactNumber.current.value = e.target;
        break;
      case "":
    }
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    //do this inCase of success response
    setIsSignUp((prev) => !prev);
  };

  return (
    <div className="modal-container">
      <div className="register-login">
        <form onSubmit={handleSubmit}>
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Name</label>
            <input type="text" onChange={handleChange} name="name" />
          </section>
          <label>Contact</label>
          <input type="text" onChange={handleChange} name="contact" />
          <label>Password</label>
          <input type="text" onChange={handleChange} name="password" />
          <section className={isSignUp ? "show" : "hidden"}>
            <label>Confirm Password</label>
            <input type="text" onChange={handleChange} name="confirmPassword" />
          </section>
          <section>
            <button onClick="submit">Submit</button>
          </section>
        </form>
      </div>
      <div className="response-info"></div>
    </div>
  );
};

export default RegistrationForm;
